    private void GetAvailableParkingSpaceID(string lat, string longt)
    {
        if (sconn.State == ConnectionState.Open)
        {
            sconn.Close();
        }
        sconn.Open();

        // string remove = area.Trim();
        //SqlCommand con = new SqlCommand("SELECT Id,title=Parking_Name,lat=lat,lng=longt,Parking_Name FROM(SELECT *,(((acos(sin((@lat*pi()/180)) * sin((lat*pi()/180))+cos((@lat*pi()/180)) * cos((lat*pi()/180)) * cos(((@longt - longt)*pi()/180))))*180/pi())*60*1.1515*1.609344) as distance FROM Admin_Rent_Space) t WHERE distance <=3", sconn);
        SqlCommand con = new SqlCommand("SELECT Id FROM(SELECT *,(((acos(sin((@lat*pi()/180)) * sin((lat*pi()/180))+cos((@lat*pi()/180)) * cos((lat*pi()/180)) * cos(((@longt - longt)*pi()/180))))*180/pi())*60*1.1515*1.609344) as distance FROM Admin_Rent_Space) t WHERE distance <=2", sconn);
        con.Parameters.AddWithValue("@lat", lat);
        con.Parameters.AddWithValue("@longt", longt);
        SqlDataAdapter da = new SqlDataAdapter(con);
        DataTable dt = new DataTable();
        da.Fill(dt);
        if (dt.Rows.Count > 0)
        {
            for (int i = 0; i < dt.Rows.Count; i++)
            {
                string isss = dt.Rows[i]["Id"].ToString();
                list.Add(isss);
            }
            Session.Remove("sess");
            Session["sess"] = list;
            Response.Redirect("Default2.aspx");
        }
        else
        {
            ScriptManager.RegisterStartupScript(this.Page, Page.GetType(), "Message", "<script type=text/javascript>window.onload = function(){alert('Sorry no parking area available for the selected region.');}</script>", false);
        }
    }

